import React from 'react'
import {Card} from 'reactstrap'
import {
	Block,
	Row,
	introCard
} from '../../util/introStyles'
import {Tooltip} from 'reactstrap'




const soci={
marginTop:'10px',
width:'100%',
WebkitTextFillColor:'azure',
color:'azure',
fontWeight:'bolder',
display:'flex',
gridGap:'3px'
			 	}



const sociBtn={
fontWeight:'bolder',
backgroundColor:'rgba(0,0,0,0)',


			 	}



class Intro extends React.Component{
		constructor(props){
			super(props)
			this.state={
			tooltipOpen:false,
			}
		}
toggle = () => this.setState({tooltipOpen:!this.state.tooltipOpen});

	render(){
	return(
		<Block>

		<Row>

		<Card style={introCard}>
		<h2 className="alt">Full-Stack Web Developer</h2>
		<p style={{fontSize:'1.5rem'}}>I am here to help start-ups, small businesses, entrepreneur's, and agencies get better business with good quality websites and satisfying user experience. I believe, to be a sucessful developer one must constantly self improve, have multiple approaches to problems, and stay up to date with technologies.</p>
		</Card>
	 	</Row>
	 	  <div>
      <p><span id="DisabledAutoHideExample" onClick={()=>this.toggle()}><button style={sociBtn}>My Social Media?</button></span></p>
      <Tooltip style={soci} placement="bottom" isOpen={this.state.tooltipOpen} autohide={false} target="DisabledAutoHideExample" >
        <i class="fab fa-youtube"></i>
        <i class="fab fa-github"></i>
        <i class="fab fa-twitter"></i>
      </Tooltip>
    </div>
      </Block>
	)

	}
}



export default Intro
