import React from 'react'
import Intro from './home/Intro.js'
import Skillz from './home/Skillz.js'
import Contact from './home/Contact.js'
import Services from './home/Services.js'
import {connect} from 'react-redux'
import {changeBtnLabel} from '../redux/actions'
import { Button, Tooltip} from 'reactstrap'

const pitchStyle={
paddingTop:'100px',
WebkitHeight:'100%',
height:'100%',
WebkitTextFillColor:'azure',
color:'azure',
fontWeight:'bolder'
			 	}

const TooltipItem = props => {
  const { item, id } = props;
  const [tooltipOpen, setTooltipOpen] = useState(false);

  const toggle = () => setTooltipOpen(!tooltipOpen);

  return (
    <span>
      <Button className="mr-1" color="secondary" id={"Tooltip-" + id}>
        {item.text}
      </Button>
      <Tooltip
        placement={item.placement}
        isOpen={tooltipOpen}
        target={"Tooltip-" + id}
        toggle={toggle}
      >
        Tooltip Content!
      </Tooltip>
    </span>
  );
};


