errMsg:"",
valOrNah1:"is-valid",
valOrNah2:"is-valid",
valOrNah3:"is-valid",
})


			return good(true)
		}else{
				this.setState({valOrNah1:"is-invalid",
valOrNah2:"is-invalid",
valOrNah3:"is-invalid",errMsg:"We Cant Send This, please check for errors",passMsg:""})
		return bad(false)


		}



	})


}



