import React from 'react'
import styled from 'styled-components'
import {Card} from 'reactstrap'


const Block = styled.div`
padding-top:50px;
flex:1;
display:flex;
-webkit-flex-direction:column;
flex-direction:column;
-webkit-justify-content:flex-start;
-webkit-align-items:flex-start;
justify-content:flex-start;
align-items:flex-start;
-webkit-text-fill-color:azure;
color:azure;

`

const Row = styled.div`
padding-top:50px;
flex:1;
display:flex;
-webkit-flex-direction:row;
flex-direction:row;
-webkit-text-fill-color:azure;
color:azure;
-webkit-grid-gap:10px;
grid-gap:10px;





@media screen and (max-width:500px){
-webkit-flex-direction:column;
flex-direction:column;

}





@media screen and (max-width:900px){

	figure{
		margin-top:-50px;

	}

}


`

const me = {
	WebkitBorderRadius:'100%',
	borderRadius:'100%',
	border:'double 3px #240090',
	WebkitBoxShadow:'0 0 10px #4df7ff',
	boxShadow:'0 0 10px #4df7ff'
}

let introCard = {
	backgroundColor:'rgba(0,0,0,.5)',
	WebkitTextFillColor:'azure',
	color:'azure',
	border:'double rgba(250,250,250,.5)',
 	WebkitBoxShadow:'2px 0 10px #888',
 	boxShadow:'2px 0 10px #888'
}



class Intro extends React.Component{


	render(){
	return(
		<Block>

		<Row>
		<figure>
		<img src={this.props.me} style={me} alt='me' width='50' height='50'/>
		<figcaption style={{fontSize:'1.3rem'}}>Guyton M. Oriji</figcaption>
		</figure>
		<Card style={introCard}>
		<h2 className="alt">A Full-Stack Web Developer</h2>
		<p style={{fontSize:'1.5rem'}}>I started out self taught for a few years, with a lot of work done for clients(private and public).
		Now I'll be graduating soon at <a href="http://LambdaSchool.com">LambdaSchool</a> and I help start-ups, small businesses, and agencies achieve high quality websites and satisfying user experience.</p>
		</Card>
	 	</Row>

      </Block>
	)

	}
}



export default Intro
