import React from 'react';
import './App.css';
import {Container} from 'reactstrap'
import {Route} from 'react-router-dom'

import styled from 'styled-components'

import logo from './img/tgc1.png'
import me from './img/me.png'
import bg from './img/bg6.jpg'
import mbg from './img/bg6mobile.jpg'
import wavio from './img/wave.png'

import Header from './Header'
import Home from './comps/'
import Projects from './comps/projects/'

import {connect} from 'react-redux'
import {getMyWork} from './redux/actions'

import {Div} from './util/cloudWaveAnimation'


let APP_COMP = styled.div`

  `





class App extends React.Component{
      constructor(props){
        super(props)
        this.state={
          mode:false
        }
      }

 DarkMode = () =>{
     if(this.state.mode){
  console.log('true',this.state.mode)
      // document.querySelector('.App').classList.remove('.lightMode')
      // document.querySelector('.App').classList.add('.darkMode')
              APP_COMP = styled.div`


 flex:1;
  -webkit-text-align: center;
  text-align: center;
  background-color:#0C0032;
  overflow-x:hidden;
  overflow-y:auto;

 background-image:url(${window.innerWidth<=500?bg:mbg});
      background-position:center;
      -moz-background-size:cover;
      -webkit-background-size:cover;
      background-size:cover;

  `
  this.setState({mode:!this.state.mode})
        }else{ 
  console.log('false',this.state.mode)
      // document.querySelector('.App').classList.add('.lightMode')
      // document.querySelector('.App').classList.remove('.darkMode')
          APP_COMP = styled.div`


 flex:1;
  -webkit-text-align: center;
  text-align: center;
  background-color:#0C0032;
  overflow-x:hidden;
  overflow-y:auto;

 background-image:url('');
      background-position:center;
      -moz-background-size:cover;
      -webkit-background-size:cover;
      background-size:cover;

  `
}  
  this.setState({mode:!this.state.mode})

        }


componentDidMount(){
        APP_COMP = styled.div`


 flex:1;
  -webkit-text-align: center;
  text-align: center;
  background-color:#0C0032;
  overflow-x:hidden;
  overflow-y:auto;

 background-image:url(${window.innerWidth<=500?bg:mbg});
      background-position:center;
      -moz-background-size:cover;
      -webkit-background-size:cover;
      background-size:cover;

  `
    }

    render(){
    return (
      <APP_COMP className="App lightMode">

      <Header logo={logo} me={[me]} mode={this.state.mode} DarkMode={()=>this.DarkMode()} />
    	<Route exact path='/-/' render={()=>{

    		return(
      <Container>
        <Home />



      </Container>
    			)
    	}}/>

      <Route exact path='/-/Projects' render={()=>{

        return (

          <Projects />

          )

      }} />

       <Div className="waveWrapper waveAnimation">
  <div className="waveWrapperInner bgTop">
    <div className="wave waveTop" style={{backgroundImage:`url(${wavio})`}}></div>
  </div>
  <div className="waveWrapperInner bgMiddle">
    <div className="wave waveMiddle" style={{backgroundImage:`url(${wavio})`}}></div>
  </div>
  <div className="waveWrapperInner bgBottom">
    <div className="wave waveBottom" style={{backgroundImage:`url(${wavio})`}}></div>
  </div>
</Div>

       </APP_COMP>



    );
  }
}



const mapStateToProps = state =>{
return {
...state
}
}


export default connect(
mapStateToProps,
{getMyWork}
)(App)
