import {MOVING,MOVED,BTN,BTNN,DARK,LIGHT,ERR} from './actions'
import bg from '../img/bg6.jpg'
import mbg from '../img/bg6mobile.jpg'


const initialState = {
	work:'',
	error:'',
	darkMode:'',
	mode:false,
	//contact btn name toggle
	btnL:'Contact Me',
	btnLL:'My Services'
}



export const reducer =  (state = initialState, action) =>{
		switch(action.type){
			case MOVING:
			return {
				...state,
			}

			case MOVED:
			return {
				...state,
				work:action.payload,
			}

			case BTN:
			return {
				...state,
				btnL:action.payload,
			}

			case BTNN:
			return {
				...state,
				btnLL:action.payload,
			}

			case DARK:
			return {
				...state,
				mode:action.payload,
				darkMode:''
			}


			case LIGHT:
			return {
				...state,
				mode:action.payload,
				darkMode:''
			}

			case ERR:
			return {
				...state,
				error:action.payload,
			}

			default:
			return state

		}







	}
