import React, { Component } from 'react';
import { Collapse,
  Navbar,
  NavbarToggler,
  Nav,
  UncontrolledDropdown,
  DropdownToggle,
  DropdownMenu,
  DropdownItem
} from 'reactstrap'
import {connect} from 'react-redux'
import {getMyWork} from './redux/actions'
import { withRouter } from 'react-router-dom';
import {Link} from 'react-router-dom'

import {brand,
 parenthStyles,
  guyStyles,
  dropdowStyles,
  me
} from './util/headerStyles'



class Header extends Component {

  constructor(props){
    super(props)
    this.state={
      currLink:'Projects',
      isOpen:false,

    }
    this.toggle = this.toggle.bind(this)
  }


 componentDidMount(){
    this.props.getMyWork() 
  }

  componentWillUpdate(){
    if(/Projects/.test(this.props.history.location.pathname)){
      this.setState({currLink:''})
    }else{
      this.setState({currLink:'Projects'})

    }
  }

  toggle(){
    this.setState({
      isOpen:!this.state.isOpen
    })
  }

  render() {


    return (
       <div>
      <Navbar  expand="md"  
      style={{
        WebkitTextFillColor:'#fff',
        color:'#fff',
        backgroundColor:'rgba(0,0,0,.5)',
      }}
      >
       <Link to='/-/' className='navbar-brand'><span style={brand} title='return true;'><figure>
    <img src={this.props.me} style={me} alt='me' width='50' height='50'/>
    </figure><b style={guyStyles}>Guyton</b> {"  "} <b style={parenthStyles}>Oriji</b></span></Link>
        <NavbarToggler onClick={this.toggle} style={{WebkitTextFillColor:'azure',color:'azure'}}>
        <i className="fas fa-ellipsis-v"></i>
        </NavbarToggler>
        <Collapse isOpen={this.state.isOpen} navbar>
          <Nav className="mr-auto" navbar>

            <UncontrolledDropdown nav inNavbar>
              <DropdownToggle nav caret >
                Games
              </DropdownToggle>
              <DropdownMenu left='true'
              style={dropdowStyles}>
              {
                this.props.work && this.props.work.map(wrk=>{

               if(wrk.action==='PLAY'){
                     return (
                     <DropdownItem style={dropdowStyles} href={wrk.link} key={wrk.id}>
                {wrk.title}
                </DropdownItem>

                    )
                  }else{return true}
                })
              }  
              </DropdownMenu>
            </UncontrolledDropdown>
          </Nav>
              <Link to={'/-/'+this.state.currLink}>{this.state.currLink}</Link>
        </Collapse>
      </Navbar>
    </div>
    )
  }
}


  const mapStateToProps = state =>{
    return {
     work:state.work
    }
  }

export default withRouter(connect(
  mapStateToProps,
    {getMyWork}
  )(Header))
